import React from 'react';
import { GoogleMap, LoadScript, Marker, InfoWindow } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '400px'
};

const atlantaCenter = {
  lat: 33.7490,
  lng: -84.3880
};

const greenSpaces = [
  {
    name: "Piedmont Park",
    position: { lat: 33.7881, lng: -84.3713 },
    size: "185 acres",
    vegetation: "Mixed forest",
    facilities: "Playground, Walking paths"
  },
  {
    name: "Grant Park",
    position: { lat: 33.7365, lng: -84.3731 },
    size: "131 acres",
    vegetation: "Native trees, Floral beds",
    facilities: "Zoo, Cyclorama"
  },
  {
    name: "Chastain Park",
    position: { lat: 33.8734, lng: -84.3973 },
    size: "268 acres",
    vegetation: "Open grass fields, Wooded areas",
    facilities: "Amphitheater, Golf course, Horse park"
  },
  // Add more green spaces as needed
];

function MapComponent() {
  const [selectedSpace, setSelectedSpace] = React.useState(null);

  return (
    <LoadScript googleMapsApiKey="AIzaSyDKnPQXQqgwlThYL2om8dykd12QHd6d9gY">
      <GoogleMap
        mapContainerStyle={containerStyle}
        center={atlantaCenter}
        zoom={12}
      >
        {greenSpaces.map(space => (
          <Marker 
            key={space.name} 
            position={space.position} 
            onClick={() => setSelectedSpace(space)}
          />
        ))}

        {selectedSpace && (
          <InfoWindow
            position={selectedSpace.position}
            onCloseClick={() => setSelectedSpace(null)}
          >
            <div>
              <h2>{selectedSpace.name}</h2>
              <p>Size: {selectedSpace.size}</p>
              <p>Vegetation: {selectedSpace.vegetation}</p>
              <p>Facilities: {selectedSpace.facilities}</p>
            </div>
          </InfoWindow>
        )}
      </GoogleMap>
    </LoadScript>
  )
}

export default React.memo(MapComponent);
